import requests

# 將 cookie 轉換為 dict 格式
cookies = {
    "ithp": "eyJ0eXAiOiJpZC10b2tlbitqd3QiLCJraWQiOiI5ZGUyNmI0MC01YTQ2LTQzOTktODE1Mi1lN2FjMGZkOGMxMzkiLCJhbGciOiJFUzI1NiJ9."
}

# 指定一個需要使用 cookies 的 URL
url = "https://vip.104.com.tw/some_protected_endpoint"

# 發送帶有 cookies 的 GET 請求
response = requests.get(url, cookies=cookies)

# 顯示回應狀態碼與部分內容
print(response.status_code)
print(response.text[:500])  # 輸出前 500 字符供確認